const items = $input.all();
if (!items || items.length === 0) {
  return [];
}

const stdout = (items[0].json.stdout || '').trim();
if (!stdout) {
  return [];
}

const courses = stdout.split('\n')
  .map(line => line.trim())
  .filter(Boolean)
  .map(course => ({ json: { courseName: course } }));

return courses;

